fn main() {
    part1();
    part2();
}

fn part1() {
    let floor: i32 = INPUT
        .chars()
        .map(|c| {
            if c == '(' {
                return 1;
            }
            -1
        })
        .sum();

    println!("Part1: Santa finishes at floor: {floor}");
}

fn part2() {
    let mut floor = 0;
    let mut position = None;

    for (i, c) in INPUT.chars().enumerate() {
        let change = if c == '(' { 1 } else { -1 };

        floor += change;
        if floor == -1 {
            position = Some(i + 1);
            break;
        }
    }

    let position = position.unwrap();
    println!("Part2: position of character that causes entering the basement: {position}");
}

